sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue == oldValue) {
        return;
    }
var ga = new GlideAjax('ClaimsUtilsAjax');
 ga.addParam('sysparm_name', 'getEmailAdress');
 ga.addParam('sysparm_user', newValue);
 ga.getXMLAnswer(callback);

}   

function callback(response){
	g_form.setValue('u_requested_for_email', response);

}
